    void refesh() {
        String k = key.getText();
        cipher.setKey(k);
        cipher.printMatrix();
        if (method.getSelectedIndex() == 0) {
            // encrypt
            cipher.setPlainText(in.getText());
            cipher.encrypt();
            out.setText(cipher.getCipher());
        } else {
            // decrypt
            cipher.setCipher(in.getText());
            cipher.decrypt();
            out.setText(cipher.getPlainText());
        }
    }

