    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:

        if (a1.getText().equals("") && a2.getText().equals("") && a3.getText().equals("") && a4.getText().equals("") && a5.getText().equals("")) {
            h.setVisible(true);
            h1.setVisible(true);
        } else {
            Control kk = new Control();
            try {

                kk.Itemes();
                JOptionPane.showMessageDialog(null, "successfully Bought you item");

            } catch (SQLException ex) {
                Logger.getLogger(CustemerHome.class.getName()).log(Level.SEVERE, null, ex);
            } catch (Exception ex) {
                Logger.getLogger(CustemerHome.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

