    private void iconActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_iconActionPerformed
        JOptionPane.showMessageDialog(null, "PLAIN_MESSAGE equvalent number: "+ JOptionPane.PLAIN_MESSAGE, "Tut", JOptionPane.PLAIN_MESSAGE);
        JOptionPane.showMessageDialog(null, "ERROR_MESSAGE equvalent number: "+ JOptionPane.ERROR_MESSAGE, "Tut", JOptionPane.ERROR_MESSAGE);
        JOptionPane.showMessageDialog(null, "INFORMATION_MESSAGE equvalent number: "+ JOptionPane.INFORMATION_MESSAGE, "Tut", JOptionPane.INFORMATION_MESSAGE);
        JOptionPane.showMessageDialog(null, "WARNING_MESSAGE equvalent number: "+ JOptionPane.WARNING_MESSAGE, "Tut", JOptionPane.WARNING_MESSAGE);
        JOptionPane.showMessageDialog(null, "QUESTION_MESSAGE equvalent number: "+ JOptionPane.QUESTION_MESSAGE, "Tut", JOptionPane.QUESTION_MESSAGE);
    }//GEN-LAST:event_iconActionPerformed

    private void typeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_typeActionPerformed
        JOptionPane.showConfirmDialog(null, "showConfirmDialog int from button[] start from 0, if cancled -1", "Tut", JOptionPane.INFORMATION_MESSAGE);
        JOptionPane.showInputDialog(null, "showConfirmDialog String if cancled null", "Tut", JOptionPane.INFORMATION_MESSAGE);
        JOptionPane.showMessageDialog(null, "showConfirmDialog void", "Tut", JOptionPane.INFORMATION_MESSAGE);
        Object[] os = {"First","Second","Third"};
        JOptionPane.showOptionDialog(null, "showOptionDialog int from object[] start from 0, if cancled -1", "Tut", 0, JOptionPane.ERROR_MESSAGE, null, os, "");
    }//GEN-LAST:event_typeActionPerformed

